import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { ToDo } from 'src/app/Models/to-do';
let ToDoDetailComponent = class ToDoDetailComponent {
    constructor(router, todoSV) {
        this.router = router;
        this.todoSV = todoSV;
        this.dataUpdate = localStorage.getItem("dataUpdate");
        if (this.dataUpdate == null) {
            this.Data = new ToDo(0, '', '', false);
        }
        else {
            this.Data = JSON.parse(this.dataUpdate);
        }
    }
    ngOnInit() {
    }
    BackFnc() {
        this.router.navigateByUrl("/todoLst");
    }
    async UpdateFnc() {
        var res = -1;
        if (this.dataUpdate == null) {
            res = await this.todoSV.Post(this.Data);
        }
        else {
            res = await this.todoSV.Put(this.Data);
        }
        if (res != -1) {
            alert("Cập nhật thành công!");
            this.router.navigateByUrl("/todoLst");
        }
        else {
            alert("Lỗi cập nhật dữ liệu!");
        }
    }
};
ToDoDetailComponent = __decorate([
    Component({
        selector: 'app-to-do-detail',
        templateUrl: './to-do-detail.component.html',
        styleUrls: ['./to-do-detail.component.scss']
    })
], ToDoDetailComponent);
export { ToDoDetailComponent };
